package numberprogrammes;

public class Swaping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//using 2 variable
		int a=1,b=2;
		a=a+b;
		b=a-b;
		a=a-b;
		System.out.println(a);
		System.out.println(b);
		
		//using 3 variables
		int i=1,j=2,k;
		k=i;
		i=j;
		j=k;
		System.out.println(i);
		System.out.println(j);

	}

}
